INSERT INTO `ht_article` VALUES ('275', '79', '/uploads/5a59bd6f9c6db.jpg', '/uploads/5a581a441b8b5.jpg', '/uploads/5a581a5f0c491.jpg', '/uploads/5a581a4fb421d.jpg', '/uploads/5a581a77bf938.jpg', '<img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112101708_39286.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112101708_25982.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112101708_34842.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112101708_31063.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112101708_50069.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112101708_56834.jpg\" alt=\"\" />', '<p class=\"MsoNormal\">\r\n	一、升级原理：\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0000pt;\">\r\n	<span>利用原厂协议，不改动原车线路、无损安装主机盒及</span>CANBUS线等。\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	二、升级后的功能：（最低十项）\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	1、 开空调升窗\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	2、100码自动升窗\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	3、 雨刷升窗\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	4、锁车未熄火提示\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	5、寻车功能\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	6、疲劳提醒\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	7、超速提醒\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	8、锁车熄火升窗\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	9、自动转向\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	10、雨刷最大时双闪\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	11、超时驾驶提醒\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	12、急刹车双闪\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	13、锁车声音提示\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:52.5000pt;\">\r\n	14、开车门双闪\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	三、售后安装：\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span>无损安装不破线，插口对接不影响原车任何功能质保两年。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	四、特别说明：\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:20.2500pt;\">\r\n	一、仅限德系车安装，如：奔驰、宝马、保时捷、奥迪、大众，其他车辆无法安装！\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:20.2500pt;\">\r\n	二、安装前请确认车型年份，个别大众车无法安装。\r\n</p>', '', '10', '2018-08-14 14:56:56', '德系车配置升级', '1980', null, '', '', '0', '', '3');
INSERT INTO `ht_article` VALUES ('276', '79', '/uploads/5a59bea0a7655.jpg', '/uploads/5a581c7254c3c.jpg', '/uploads/5a581c7ac3641.jpg', '/uploads/5a581c8164060.jpg', '/uploads/5a581c867eb9f.jpg', '<p class=\"15\" style=\"margin-left:36.0000pt;text-indent:-36.0000pt;\">\r\n	一、 <b><span>升级装汽车</span>LED大灯优势特点</b><b></b> \r\n</p>\r\n<p class=\"15\" style=\"margin-left:77.2500pt;text-indent:-36.0000pt;\">\r\n	1、 <span>无损安装</span> <span>直接对插，不破拆原车电路</span> \r\n</p>\r\n<p class=\"15\" style=\"margin-left:77.2500pt;text-indent:-36.0000pt;\">\r\n	2、 <span>节能环保</span> 24W &nbsp;节能60%以上。\r\n</p>\r\n<p class=\"15\" style=\"margin-left:77.2500pt;text-indent:-36.0000pt;\">\r\n	3、 <span>高亮度</span> 2600流明，是卤素灯亮度的3倍\r\n</p>\r\n<p class=\"15\" style=\"margin-left:77.2500pt;text-indent:-36.0000pt;\">\r\n	4、 <span>四高穿透</span> <span>远光射程</span>200米\r\n</p>\r\n<p class=\"15\" style=\"margin-left:77.2500pt;text-indent:-36.0000pt;\">\r\n	5、 <span>长寿命</span> 30000小时，终生免维护。\r\n</p>\r\n<p class=\"15\" style=\"margin-left:77.2500pt;text-indent:-36.0000pt;\">\r\n	6、 <span>高稳定</span> <span>美国科锐进口灯珠，热电分离散热技术</span> \r\n</p>\r\n<p class=\"15\" style=\"margin-left:77.2500pt;text-indent:-36.0000pt;\">\r\n	7、&nbsp;360调光 &nbsp;&nbsp;&nbsp;调光简单，更聚光\r\n</p>\r\n<p class=\"15\" style=\"margin-left:77.2500pt;text-indent:-36.0000pt;\">\r\n	8、 <span>零秒启动</span> <span>无延时，支持频闪</span> \r\n</p>\r\n<p class=\"15\" style=\"margin-left:77.2500pt;text-indent:-36.0000pt;\">\r\n	9、 <span>冷光源</span> <span>避免高温炙烤灯罩。</span> \r\n</p>\r\n<p class=\"15\" style=\"margin-left:77.2500pt;text-indent:-36.0000pt;\">\r\n	10、 <span>三年保换</span> <span>质量问题</span>3年保换\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:21pt;vertical-align:baseline;\">\r\n	<img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102653_28652.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102654_16212.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102656_38226.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102656_56535.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102657_38207.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102659_59465.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102659_93666.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102659_21760.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102659_26512.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102700_56774.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102700_22587.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102701_59905.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102702_55027.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180112/20180112102703_59350.jpg\" alt=\"\" /> \r\n</p>', '<p class=\"MsoNormal\" style=\"margin-left:21pt;vertical-align:baseline;\">\r\n	需到店安装\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:21pt;vertical-align:baseline;\">\r\n	1<span>、</span><span>德系车等车型有些需要加繲码器，费用</span>60<span>元一对。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:21pt;vertical-align:baseline;\">\r\n	3<span>、部分车型需要加支架或者扣碗一对</span><span>15</span><span>元。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:21pt;vertical-align:baseline;\">\r\n	4<span>、</span><span>12V</span><span>和</span><span>24V</span><span>车辆均可使用，但个别车型可能装不了，请您提前确认。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:21pt;vertical-align:baseline;\">\r\n	5<span>、免费安装，个别车型需要加装繲码器或卡口，费用另计。</span> \r\n</p>', '', '0', '2018-08-14 15:04:30', '高配版汽车LED大灯升级  （第二套半价）', '860', null, '', '', '0', '', '3');
