INSERT INTO `ht_article` VALUES ('297', '79', '/uploads/5a59b6d7f2fd2.jpg', '/uploads/5a599d5ed739a.jpg', '/uploads/5a599d634a131.jpg', '/uploads/5a599d6c3acd3.jpg', '', '<p class=\"MsoListParagraph\" style=\"margin-left:25.05pt;text-indent:-18.0pt;\">\r\n	<img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120637_14726.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120637_43563.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120637_45153.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120637_81716.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120637_56112.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120638_21439.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120638_82625.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120638_34367.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120638_67818.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120639_92740.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120639_43236.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120639_37704.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120639_76231.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120640_90466.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120640_16506.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120640_22540.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120641_95756.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180117/20180117120641_55807.jpg\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	<br />\r\n</p>', '<p class=\"MsoListParagraph\" style=\"margin-left:25.05pt;text-indent:-18pt;\">\r\n	产品卖点：智能人机对话 刚性实用需求 导航、行车记录仪 电子狗！\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 主镜头光圈:&nbsp;F2.2\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 品牌:&nbsp;JADO/捷渡\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 型号:&nbsp;D210\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 操作系统:&nbsp;阿里YunOS\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 套餐:&nbsp;官方标配\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 安装类型: 绑带式后视镜\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 影像分辨率:&nbsp;1080p\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 摄像像素:&nbsp;500万\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 画面视角:&nbsp;140\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 运行内存:&nbsp;1GB\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 镜头数量:&nbsp;单前镜头\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 内存卡: &nbsp;不带内存卡\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 屏幕尺寸:&nbsp;6.86英寸\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 核心数:&nbsp;双核心\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 地图类型:&nbsp;高德\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 选配：内存卡、倒车影像+后摄像头\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:3.25pt;background:white;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:25.05pt;text-indent:-18pt;\">\r\n	产品亮点：\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:59.95pt;text-indent:-21pt;\">\r\n	l&nbsp; 全语音声控、智能人机对话\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:59.95pt;text-indent:-21pt;\">\r\n	l&nbsp;7寸大屏、阿里巴巴旗下阿里云系统\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:59.95pt;text-indent:-21pt;\">\r\n	l&nbsp; 导航、前行车记录仪、电子狗 实用功能\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:59.95pt;text-indent:-21pt;\">\r\n	l&nbsp; 地图自动升级、实时路况、虾米音乐、喜马拉雅电台、微信接人\r\n</p>', '', '9', '2018-09-03 09:41:18', '捷渡智能后视云镜 ', '699', null, '', '', '0', '', '3');
