INSERT INTO `ht_article` VALUES ('327', '70', '/uploads/5a59e649a7b8c.jpg', '/uploads/5a59dbd27f7c9.jpg', '/uploads/5a59dbddb8ac0.jpg', '', '', '<img src=\"/Public/kindeditor/php/../attached/image/20180113/20180113181057_78466.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180113/20180113181057_33052.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180113/20180113181102_12210.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180113/20180113181106_66060.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180113/20180113181122_65296.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180113/20180113181126_35897.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180113/20180113181128_82193.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180113/20180113181129_74948.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180113/20180113181140_33518.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180113/20180113181141_34906.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180113/20180113181144_16036.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180113/20180113181150_70150.jpg\" alt=\"\" />', '', '', '200', '2018-08-14 14:35:47', '趴趴狗PAPAGO SAFE760', '1080', null, '', '', '0', '', '3');
INSERT INTO `ht_article` VALUES ('337', '79', '/uploads/5b8c9b9a3089c.jpg', '/uploads/5b8c9ba1aa92c.jpg', '/uploads/5a5fff0fa24bf.png', '/uploads/5a5fff14d3e98.png', '/uploads/5a5fff1e69254.jpg', '<img src=\"/Public/kindeditor/php/../attached/image/20180119/20180119164210_87994.png\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180118/20180118095806_61742.png\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180118/20180118095806_83602.png\" alt=\"\" />', '<p class=\"15\" style=\"margin-left:18pt;text-indent:-18pt;\">\r\n	<!--[if !supportLists]-->1.&nbsp;<!--[endif]--><b>为什么说速马力是燃油汽车的必需品？</b><b></b> \r\n</p>\r\n<p class=\"15\" style=\"margin-left:18pt;text-indent:1584pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"15\" style=\"margin-left:18pt;text-indent:1584pt;\">\r\n	因在汽车没有使用速马力以前，我们常见的一些车辆问题，比如说：积碳、油门响应速度衰减、动力下降、噪音变大、油耗上升、换挡冲击强烈、三元催化中毒、排放超标、驾驶感下降等等，我们一般都会归疚于车辆老化，甚至觉得这是车辆使用的正常现象！殊不知，很多时候都是因为车辆的匹配、工艺、设计等环节技术包括理念不足所致！当然也有成本原因。\r\n</p>\r\n<p class=\"15\" style=\"margin-left:18pt;text-indent:1584pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"15\" style=\"margin-left:18pt;text-indent:1584pt;\">\r\n	速马力产品的推出，首先是内燃机工业理论的创新，或者说至少是与世界一流同步，其用催化燃油的方式，解决了发动机快速燃烧的理论问题，由此，本质优化了发动机工况并产生一系列良性连锁反应！\r\n</p>\r\n<p class=\"15\" style=\"margin-left:18pt;text-indent:1584pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"15\" style=\"margin-left:18pt;text-indent:1584pt;\">\r\n	<span>车主的感觉就是：包括新车在内，动力提升了，噪音、排放降低了，油耗下降了，车感焕然新生！殊不知更重要的是：前面所说的那些常见的、我们之前以为的</span>“自然而然的”问题，也都失去了产生的条件！\r\n</p>\r\n<p class=\"15\" style=\"margin-left:18pt;text-indent:1584pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"15\" style=\"margin-left:18pt;text-indent:1584pt;\">\r\n	所以说：车装速马力，是一台汽车完工的最后一道成品工序！只是遗憾地暂时要我们车主自行去完成！\r\n</p>\r\n<p class=\"15\" style=\"margin-left:18pt;text-indent:0pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"15\" style=\"margin-left:18pt;text-indent:0pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"15\" style=\"margin-left:18pt;text-indent:0pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"15\" style=\"margin-left:18pt;text-indent:-18pt;\">\r\n	<!--[if !supportLists]-->2.&nbsp;<!--[endif]-->实体店面地址：西安市雁塔区长安西路与东仪路十字向南100米&nbsp;东仪路西\r\n</p>', '', '98', '2018-09-03 10:25:41', 'SmartLife速马力燃油催化器 HJ6', '4800', null, '', '', '1', '', '3');
