INSERT INTO `ht_article` VALUES ('416', '79', '/uploads/5a77b2e55d611.jpg', '/uploads/5a77b36f2282e.jpg', '/uploads/5a77b3764d0dc.jpg', '/uploads/5a77b37f02127.jpg', '/uploads/5a77b38a80f4a.jpg', '<p class=\"15\">\r\n	<b><span>利事特双色</span>LED大灯:</b><b></b> \r\n</p>\r\n<p class=\"15\">\r\n	<span>无损安装</span> <span>直接对插，不破拆原车电路</span> \r\n</p>\r\n<p class=\"15\">\r\n	<span>双色变光</span> <span>正常天气用白光照得亮，雨雾雪天用黄光穿透力强。</span> \r\n</p>\r\n<p class=\"15\">\r\n	<span>高亮度</span> 2800流明，是卤素灯亮度的3倍\r\n</p>\r\n<p class=\"15\">\r\n	4四高穿透 &nbsp;&nbsp;&nbsp;远光射程200米\r\n</p>\r\n<p class=\"15\">\r\n	<span>长寿命</span> 30000小时，终生免维护。\r\n</p>\r\n<p class=\"15\">\r\n	<span>高稳定</span> <span>飞利浦芯片，热电分离散热技术</span> \r\n</p>\r\n<p class=\"15\">\r\n	360调光 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;调光简单，更聚光\r\n</p>\r\n<p class=\"15\">\r\n	<span>零秒启动</span> <span>无延时，支持频闪</span> \r\n</p>\r\n<p class=\"15\">\r\n	<span>冷光源</span> <span>避免高温炙烤灯罩。</span> \r\n</p>\r\n<p class=\"15\">\r\n	<span>两年保换</span> <span>质量问题</span>3年保换\r\n</p>\r\n<p class=\"MsoNormal\" style=\"vertical-align:baseline;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:21pt;vertical-align:baseline;\">\r\n	<br />\r\n</p>\r\n<img src=\"/Public/kindeditor/php/../attached/image/20180205/20180205092854_18129.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180205/20180205092855_46092.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180205/20180205092855_91062.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180205/20180205092855_93353.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180205/20180205092856_38536.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180205/20180205092856_20870.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180205/20180205092856_23057.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180205/20180205092856_25446.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180205/20180205092857_16869.jpg\" alt=\"\" /><img src=\"/Public/kindeditor/php/../attached/image/20180205/20180205092857_15504.jpg\" alt=\"\" />', '<p class=\"MsoNormal\" style=\"margin-left:21pt;vertical-align:baseline;\">\r\n	1、德系车等车型有些需要加繲码器，费用60元一对。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:21pt;vertical-align:baseline;\">\r\n	2、部分车型需要加支架或者扣碗一对15元。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:21pt;vertical-align:baseline;\">\r\n	3、12V和24V车辆均可使用，但个别车型可能装不了，请您提前确认。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:21pt;vertical-align:baseline;\">\r\n	4、黄白光切换原车开关直接控制，一关一开变色！\r\n</p>', '', '0', '2018-08-14 14:35:26', '利事特智能双光LED大灯升级  买两套第二套半价975元', '975', null, '', '下单此产品直接为两套优惠价', '0', '', '3');
