INSERT INTO `ht_article` VALUES ('452', '80', '/uploads/5ba0b551297b6.jpg', '/uploads/5ba0b5da54ed1.jpg', '', '', '', '<p>\r\n	<span style=\"font-size:16px;\">亲爱的各位朋友：在赴韩国旅游之前，现将旅途中所应注意的事项向您作如下说明</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">行程：</span><span style=\"font-size:16px;\">1、本社有权根据景点节假日休息/关门调整行程+自费的游览先后顺序，但游览内容不会减少，标准不会降低；2、行程景点实际游览为最短时间，以行程中标注时间为准；3、根据国内+国际航班团队搭乘要求，团队通常须提前3-3.5小时到达机场办理登机手续</span><br />\r\n<span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">宾馆：</span><span style=\"font-size:16px;\">1、行程中所列酒店星级标准为当地酒店评定标准；2、韩国当地的4花酒店大堂都比较小，房间也较小，但是很干净；</span><br />\r\n<span style=\"font-size:16px;\">退费：</span><span style=\"font-size:16px;\">1、如遇天气、战争、罢工、地震等人力不可抗力因素无法游览，我社将按照旅行社协议，退还未游览景点门票费用，免费参观的景点无法退票；但赠送项目费用不退；2、游客因个人原因临时自愿放弃游览，酒店住宿、餐、车等费用均不退还；3、如游客脱团 出发前通知操作员 &nbsp;购物当天脱团需缴纳1500元/人/天，非购物当天脱团800元/人/天</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">其它：</span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">质量反馈表，我社处理游客意见，以游客交回的《团队质量反馈表》为依据，请您秉着公平、公正、实事求是的原则填写《团队质量反馈表》；</span><span style=\"font-size:16px;\"></span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"https://timgsa.baidu.com/timg?image&amp;quality=80&amp;size=b9999_10000&amp;sec=1537268666712&amp;di=13654a68e9309707e294d52cded2f330&amp;imgtype=0&amp;src=http%3A%2F%2Fpic2.40017.cn%2Fline%2Fadmin%2F2014%2F11%2F04%2F09%2FPer1za.jpg\" /> \r\n</p>\r\n<p>\r\n	<img src=\"https://timgsa.baidu.com/timg?image&amp;quality=80&amp;size=b9999_10000&amp;sec=1537268666712&amp;di=3510a888f57bdd278de6296d15b25978&amp;imgtype=0&amp;src=http%3A%2F%2Fimg.zcool.cn%2Fcommunity%2F01c54f58b2c68fa801219c778e8efb.jpg%401280w_1l_2o_100sh.jpg\" /> \r\n</p>\r\n<p>\r\n	<img src=\"https://timgsa.baidu.com/timg?image&amp;quality=80&amp;size=b9999_10000&amp;sec=1537268666711&amp;di=491b53f0513580bb3cbfc11b2382e420&amp;imgtype=0&amp;src=http%3A%2F%2Fimg.pconline.com.cn%2Fimages%2Fupload%2Fupc%2Ftx%2Fitbbs%2F1408%2F15%2Fc14%2F37461333_1408081134503_mthumb.jpg\" /> \r\n</p>', '<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>报价包含：</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">1、</span><span style=\"font-size:16px;\">签证：</span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">免签</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">2、</span><span style=\"font-size:16px;\">机票：</span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">国际机票（团队经济舱，含机场建设税）</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">3、</span><span style=\"font-size:16px;\">酒店：</span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">韩国当地四花酒店</span><span style=\"font-size:16px;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">4、</span><span><span style=\"font-size:16px;\">用餐：</span><span style=\"font-size:16px;\">中华料理；8-10人一桌； &nbsp;&nbsp;韩国特色料理 4-6人一桌&nbsp;</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">5、</span><span style=\"font-size:16px;\">门票：</span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">日程内所含</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">6、</span><span style=\"font-size:16px;\">导游：</span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">全程中文讲解（普通话）</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">7、</span><span style=\"font-size:16px;\">用车：</span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">境外旅游巴士及外籍司机；（旅游车有暖气，空调制冷力度强 ）前后排位子距离宽阔</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">8、</span><span style=\"font-size:16px;\">服务费：</span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">司机导游服务费</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">9、</span><span style=\"font-size:16px;\">保险：</span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">旅行社责任险</span><span style=\"font-size:16px;\"></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><strong>报价不含：</strong></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">1、</span><span style=\"font-size:16px;\">护照费用</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">2、</span><span style=\"font-size:16px;\">自费项目</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">3、</span><span style=\"font-size:16px;\">离境税360元/人</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">4、</span><span style=\"font-size:16px;\">各种私人消费如：洗衣，理发，电话，饮料，烟酒，付费电视，行李搬运等私人费用</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">5、</span><span style=\"font-size:16px;\">费用不包括旅游者因违约、自身过错、自由活动期间内行为或自身疾病引起的人身和财产损失</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">6、</span><span style=\"font-size:16px;\">单间差1000元全程/间</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">7、</span><span style=\"font-size:16px;\">济州岛边检拒接入境所产生的一切费用</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">8、</span><span style=\"font-size:16px;\">旅游意外责任险</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">9、</span><span style=\"font-size:16px;\">因第三方原因或因不可抗力因素而需要更改行程所产生的一切费用（包括但不仅限于自然灾害、航班延误或取消、火车或轮故障因交通事故造成的道路堵塞等）。</span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">10、儿童</span><span style=\"font-size:16px;\">&nbsp;2-12周岁不占床与成人同价 &nbsp;&nbsp;占床 +800/人 &nbsp;&nbsp;&nbsp;12-18周岁必须占床&nbsp;+800/人&nbsp;&nbsp;&nbsp;65岁以上老人 +400/人&nbsp;&nbsp;</span><span style=\"font-size:16px;\"></span></span>\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:16px;\">全程绝不允许脱团，购物当天脱团需缴纳1500元/人/天，非购物当天脱团800元/人/天&nbsp;</span><span style=\"font-size:16px;\"></span></span>\r\n</p>', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;\"><strong>D</strong></span><span style=\"font-size:16px;\"><strong>1</strong></span><span style=\"font-size:16px;\"><strong>西安咸阳国际机场集合</strong></span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<p class=\"MsoNormal\">\r\n		<span style=\"font-size:16px;\">请各位贵宾于当天晚上</span><span style=\"font-size:16px;\"> 23:</span><span style=\"font-size:16px;\">3</span><span style=\"font-size:16px;\">0 抵达西安咸阳机场 T3 航站楼 32</span><span style=\"font-size:16px;\">1</span><span style=\"font-size:16px;\">&nbsp;</span><span style=\"font-size:16px;\">门内准时集合。由专业领队办理登机手续，</span> <span style=\"font-size:16px;\">搭乘国际航班</span> <span style=\"font-size:16px;\">真航空直飞东方夏威夷之称的著名旅游度假胜地</span><span style=\"font-size:16px;\">—济州岛！</span><span style=\"font-size:16px;\">参考航班：</span><span style=\"font-size:16px;\">LJ172 &nbsp;02:20-06:05&nbsp;</span>\r\n	</p>\r\n	<p class=\"MsoNormal\">\r\n		<span style=\"font-size:16px;\"><strong>D2</strong></span><span style=\"font-size:16px;\"><strong>&nbsp;</strong></span><span style=\"font-size:16px;\"><strong>济州</strong></span>\r\n	</p>\r\n	<p class=\"MsoNormal\">\r\n		<span style=\"font-size:16px;\">抵达济州岛之后在优秀韩国中文导游带领下，先去吃早餐，随后汗蒸房休息！</span>\r\n	</p>\r\n	<p class=\"MsoNormal\">\r\n		<span style=\"font-size:16px;\">【海水温泉热疗</span><span style=\"font-size:16px;\">/</span><span style=\"font-size:16px;\">汗蒸</span><span style=\"font-size:16px;\">】</span><span style=\"font-size:16px;\">（大约</span><span style=\"font-size:16px;\">2~3小时） 韩国人每周必去的，洗去一夜疲劳</span>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n		<span style=\"font-size:16px;\">【汉拿山国立公园】【汉拿山树木园】</span><span style=\"font-size:16px;\">郁郁葱葱的常绿阔叶树形成的乔木园、四季开花的花木园、稀有特产树种园、等，在这里一年四季都可与大自然亲密接触、美丽邂逅。（</span><span style=\"font-size:16px;\"> 60 分钟）</span>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n		<span style=\"font-size:16px;\">【</span><span style=\"font-size:16px;\">GD权志龙咖啡】</span><span style=\"font-size:16px;\">权志龙</span><span style=\"font-size:16px;\">GD</span><span style=\"font-size:16px;\">在济州岛</span><span style=\"font-size:16px;\">2015年10月开设,这间店位于济州岛西北侧一个名为涯月的地方,是一家名副其实的海景咖啡店，风景无敌。可以一边享受浓郁咖啡，一边看着蔚蓝的大海！（30分钟）</span>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n		<span style=\"font-size:16px;\">【</span><span style=\"font-size:16px;\">雪绿茶博物馆</span><span style=\"font-size:16px;\">】</span><span style=\"font-size:16px;\">济州规模最大的茶博物馆，茶文化一直在中国源远流长，韩国文化常年受到中国的影响，茶也成为了他们的文化其中之一的重要部分。（</span><span style=\"font-size:16px;\">40 分钟）</span>\r\n	</p>\r\n	<p class=\"MsoNormal\">\r\n		<span style=\"font-size:16px;\">【偶来小路】</span><span style=\"font-size:16px;\">充满乐趣的济州岛上有围绕整个岛屿的散步路</span><span style=\"font-size:16px;\">\"Olle\"路。 沿着偶来Olle路走，沿途可以欣赏大海、树林、村落等各种各样的风光。（20分钟）</span>\r\n	</p>\r\n<span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">早餐：含</span><span style=\"font-size:16px;\"> &nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;\">午餐：含</span><span style=\"font-size:16px;\"> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;\">晚餐：含</span><span style=\"font-size:16px;\"> &nbsp;&nbsp;</span><span style=\"font-size:16px;\">&nbsp;&nbsp;</span><span style=\"font-size:16px;\">酒店</span><span style=\"font-size:16px;\">-济州四花或同级酒店</span>\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<p class=\"MsoNormal\">\r\n		<span style=\"font-size:16px;\"><strong>D3</strong></span><span style=\"font-size:16px;\"><strong>济州岛</strong></span>\r\n	</p>\r\n	<p class=\"MsoNormal\">\r\n		<span style=\"font-size:16px;\">【城山日出峰】</span><span style=\"font-size:16px;\">世界上最大的海中火山口，于</span><span style=\"font-size:16px;\">2000年7月19日被指定为地方天然纪念物。2007年列入UNESCO世界自然遗产，济州岛必去景点之一。在日出峰顶观看日出，令人叹为观止的美丽和庄严，仿佛带你走进一个美妙绝伦的境地;峰顶上可看到海岸线辽阔的大海和草原上壮丽的田园风光;此外，每年的元旦前后，这里举行的城山日出节，盛大的节日盛典和独特的民俗文化，定会令你终身难忘。（60分钟）</span>\r\n	</p>\r\n	<p class=\"MsoNormal\">\r\n		<span style=\"font-size:16px;\">【涉地可支】</span><span style=\"font-size:16px;\">地名由意为</span><span style=\"font-size:16px;\">“人才辈出之地势”的“涉地”和意为“岬角”的济州方言“可之”结合而成。同时也有“风之岗”的别称。所在的海岸悬崖上是一片宽阔的草地，这里一棵树也看不到，海岸上耸立着一块叫仙石的岩石！（约30分钟）</span>\r\n	</p>\r\n	<p class=\"MsoNormal\">\r\n		<span style=\"font-size:16px;\">【城邑民俗村】</span><span style=\"font-size:16px;\">完全反映了济州岛独特的居住文化。在这里可以看到那看似堆得稀稀疏疏的挡风石墙（黑熔岩石），为了防止风直接进入里屋而建得又直又弯的</span><span style=\"font-size:16px;\">“奥来”（窄胡同），还有成为济州岛之象征的石神像（Dolharubang）。是展现济州人生活和足迹文化遗产。（40分钟）</span>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n		<span style=\"font-size:16px;\">【金宁海水浴场】</span><span style=\"font-size:16px;\">济州岛的海水浴场，大家首先想到的是碧蓝色。但，金宁海水浴场除了碧蓝色之外，还有一个代表性的颜色</span><span style=\"font-size:16px;\">——钴蓝色。有大片的岩石延伸到海底，所以才会有迷人的钴蓝色出现。比起因为碧蓝色而有名的咸徳海水浴场以及挟才海水浴场，金宁海水浴场能看到的海的颜色更多。也正是因为这个原因，来金宁海水浴场拍海报的人特别多。（120分钟</span><span style=\"font-size:16px;\">）</span>\r\n	</p>\r\n	<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n		<span style=\"font-size:16px;\">【橘子园】</span><span style=\"font-size:16px;\">这就是经典韩剧《我的女孩》中周幼琳采摘橘子的橘园，想和韩剧女主角一样体验采摘橘子的乐趣，那么</span> <span style=\"font-size:16px;\">这一定是济州之旅不可错过的景点。</span><span style=\"font-size:16px;\">（</span><span style=\"font-size:16px;\">30分钟）</span>\r\n	</p>\r\n	<p class=\"MsoNormal\">\r\n		<span style=\"font-size:16px;\">【跆拳道秀】</span><span style=\"font-size:16px;\">济州岛上举行的韩国跆拳道表演</span><span style=\"font-size:16px;\">,被海外多个国家的观众们所称赞的最具特色的韩国文化产品！（40分钟）</span>\r\n	</p>\r\n	<p class=\"MsoNormal\">\r\n		<span style=\"font-size:16px;\">早餐：含</span><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;\">午餐：含</span><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;\">晚餐：含</span><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;\">&nbsp;</span><span style=\"font-size:16px;\">酒店</span><span style=\"font-size:16px;\">-</span><span style=\"font-size:16px;\">济州四花或同级酒店</span>\r\n	</p>\r\n	<p class=\"MsoNormal\">\r\n		<p class=\"MsoNormal\">\r\n			<span style=\"font-size:16px;\"><strong>D4</strong></span><span style=\"font-size:16px;\"><strong>济州岛</strong></span>\r\n		</p>\r\n		<p class=\"MsoNormal\">\r\n			<span style=\"font-size:16px;\">【龙头岩】</span><span style=\"font-size:16px;\">火山熔岩到大海边冷却凝固后经过海蚀形成龙头形，佛神话传说中的一条巨龙，屹立于波涛澎湃的海岸，模样有如龙头，相传四龙因触怒天神而化为岩石。是济州岛旅游标志。（</span><span style=\"font-size:16px;\">20分钟）</span>\r\n		</p>\r\n		<p class=\"MsoNormal\">\r\n			<span style=\"font-size:16px;\">【韩国泡菜</span><span style=\"font-size:16px;\">+</span><span style=\"font-size:16px;\">传统文化体验】</span><span style=\"font-size:16px;\">贵宾均将成为今天的最佳男女主角，换上古代传统的结婚礼物，于古色古香之传统韩屋式的场景内，愉快的扑捉美丽的倩影。随后由专人解说泡菜的功效及腌制泡菜的过程，让大家可试吃现做的泡菜，学习了解泡菜全部制作过程。（</span><span style=\"font-size:16px;\">30分钟）</span>\r\n		</p>\r\n		<p class=\"MsoNormal\">\r\n			<span style=\"font-size:16px;\">【高丽人参专</span><span style=\"font-size:16px;\">门</span><span style=\"font-size:16px;\">店</span><span style=\"font-size:16px;\">+</span><span style=\"font-size:16px;\">护肝宝专门店】</span><span style=\"font-size:16px;\">韩国独有的韩国国宝（高麗人参），</span><span style=\"font-size:16px;\">“高丽人蔘”生长的地理条件优良。独特的地理优势使高麗人蔘得到充份發育，性质温和，适合各种体质。护肝宝专为喜爱喝酒人群准备，饮酒前吃一粒，有护肝保肝作用,韩国人必备良药。（各40分钟）</span>\r\n		</p>\r\n		<p class=\"MsoNormal\">\r\n			<span style=\"font-size:16px;\">【韩国化妆品】</span><span style=\"font-size:16px;\">相信爱美的女性朋友们一定可以在这挑选最新款最</span><span style=\"font-size:16px;\">HITO彩妆品，让辛苦的上班族及学生族群在忙禄生活中一样可以打造出时尚彩妆，让您永远跟上时代潮流尖端。（60分钟）</span>\r\n		</p>\r\n		<p class=\"MsoNormal\">\r\n			<span style=\"font-size:16px;\">【新罗免税店</span><span style=\"font-size:16px;\">THE SHILLA】</span><span style=\"font-size:16px;\">汇聚了各种国际品牌，是韩国最具代表性的免税店，欢乐畅享购物的乐趣。（</span><span style=\"font-size:16px;\">90</span><span style=\"font-size:16px;\">分钟）</span><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span>\r\n		</p>\r\n		<p class=\"MsoNormal\">\r\n			<span style=\"font-size:16px;\">【莲洞步行街】</span><span style=\"font-size:16px;\">又名保健路（莲洞</span><span style=\"font-size:16px;\">7路），是济州岛济州市区最热闹的一条商业街,,各种特色海鮮、衣服、饰品、鞋子、手工艺品等济州岛特色，自由逛街，应有尽有。（40分钟）</span>\r\n		</p>\r\n		<p class=\"MsoNormal\">\r\n			<span style=\"font-size:16px;\">早餐：含</span><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;\">午餐：含</span><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;\">晚餐：</span><span style=\"font-size:16px;\">&nbsp;</span><span style=\"font-size:16px;\">敬请自理</span><span style=\"font-size:16px;\"> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;\">酒店</span><span style=\"font-size:16px;\">-</span><span style=\"font-size:16px;\">济州四花或同级酒店</span>\r\n		</p>\r\n		<p class=\"MsoNormal\">\r\n			<span style=\"font-size:16px;\"><strong>D5 </strong></span><span style=\"font-size:16px;\"><strong>济州岛</strong></span>\r\n		</p>\r\n<span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">济州一天自由活动</span><span style=\"font-size:16px;\">（</span> <span style=\"font-size:16px;\">全天不含车、导、餐</span><span style=\"font-size:16px;\">）</span><span style=\"font-size:16px;\">温馨提示</span><span style=\"font-size:16px;\">:自由活动期间需要注意自身和家人的人身和财产安</span><span style=\"font-size:16px;\">全！</span>\r\n	</p>\r\n	<p class=\"MsoNormal\">\r\n		<p style=\"text-align:start;text-indent:0px;\">\r\n			<p class=\"MsoNormal\">\r\n				<span style=\"font-size:16px;\"><strong>D6</strong></span><span style=\"font-size:16px;\"><strong>济州</strong></span><span style=\"font-size:16px;\"><strong>—</strong></span><span style=\"font-size:16px;\"><strong>西安</strong></span> <span style=\"font-size:16px;\"><strong>&nbsp;&nbsp;</strong></span><span style=\"font-size:16px;\"><strong>参考航班：</strong></span><span style=\"font-size:16px;\"><strong>LJ171 &nbsp;22:35-01:05+1&nbsp;</strong></span>\r\n			</p>\r\n			<p class=\"MsoNormal\">\r\n				<span style=\"font-size:16px;\">【药泉寺】</span><span style=\"font-size:16px;\">是济州第一大寺，因为有治疗百病的神水，所以被称为药泉寺。（</span><span style=\"font-size:16px;\">40分钟）</span>\r\n			</p>\r\n			<p class=\"MsoNormal\">\r\n				<span style=\"font-size:16px;\">【柱状节理带】</span><span style=\"font-size:16px;\">世界文化遗产，济州岛的自然保护对象。岩浆从火山口喷出，流入西归浦沿岸，与海水急速冷却，发生收缩作用后形成的一个个巨大的玄武岩柱结构。（</span><span style=\"font-size:16px;\">30分钟）</span>\r\n			</p>\r\n			<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n				<span style=\"font-size:16px;\">【</span><span style=\"font-size:16px;\">泰迪熊博物馆</span><span style=\"font-size:16px;\">】</span><span style=\"font-size:16px;\">泰迪熊为主题的博物馆，展示馆里再现了玩具熊登场的</span><span style=\"font-size:16px;\">20世纪人类历史上的11件重大事件，还有世界泰迪熊的时装表演，有穿着[LV路易·威登]及世界各国传统婚礼的泰迪熊等可爱的熊。（30分钟）</span>\r\n			</p>\r\n			<p class=\"MsoNormal\" align=\"justify\" style=\"text-align:justify;\">\r\n				<span style=\"font-size:16px;\">【济州免税店</span><span style=\"font-size:16px;\">JTO】</span><span style=\"font-size:16px;\">汇聚了各种国际品牌，是韩国最具代表性的免税店，欢乐畅享购物的乐趣，内还设有济州最大的赌场。（</span><span style=\"font-size:16px;\">90</span><span style=\"font-size:16px;\">分钟）</span><span style=\"font-size:16px;\">&nbsp; </span><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span>\r\n			</p>\r\n			<p class=\"MsoNormal\">\r\n				<span style=\"font-size:16px;\">【土特产】</span><span style=\"font-size:16px;\">（</span><span style=\"font-size:16px;\">30分钟）海带 泡菜 食品 厨房用品等精制的韩国生活用品超市，购买的东西可以打包一个箱子拎回方便。</span>\r\n			</p>\r\n			<p class=\"MsoNormal\">\r\n				<span style=\"font-size:16px;\">按导游指定时间地点集合</span><span style=\"font-size:16px;\">前往机场，办理乘机手续，乘国际航班返回西安；</span>\r\n			</p>\r\n			<p class=\"MsoNormal\">\r\n				<span style=\"font-size:16px;\">早餐：含</span><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;\">午餐：含</span><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16px;\">晚餐：</span><span style=\"font-size:16px;\">敬请自理</span>\r\n			</p>\r\n			<p class=\"MsoNormal\">\r\n				<i><span style=\"font-size:16px;\"><strong>&nbsp;</strong></span></i><span style=\"font-size:16px;\"><strong>以上行程在不减少日程内容情况下</strong></span> <span style=\"font-size:16px;\"><strong>顺序有所变动</strong></span> <span style=\"font-size:16px;\"><strong>具体当地导游合理调整</strong></span><span style=\"font-size:16px;\"><strong>.请谅解~！</strong></span>\r\n			</p>\r\n		</p>\r\n	</p>\r\n</p>', '100', '2018-09-18 17:05:01', '韩国-济州岛4晚6日', '3380', '30', null, '', '1', null, '1');
