INSERT INTO `ht_huodong` VALUES ('34', '带上心爱的ta一起来玩儿“花”', '/Uploads/newsPicture/2018-01-22/5a65574b0460f.jpg', '69', '李卫', '1', '1', '1', '1', '1', '2018-01-23 16:53:37', '2018/1/27', '高新路51号尚中心', '<h3 style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;font-family:FangSong_GB2312;\">瓶花艺术：在鲜花的香氛气息里做一件居家艺术品<br />\r\n	<p class=\"\">\r\n		<span style=\"font-size:9px;font-family:NSimSun;\">这是活动发起人，李卫发起的#瓶花艺术#活动</span> \r\n	</p>\r\n	<p class=\"\">\r\n		<span style=\"font-size:9px;font-family:NSimSun;\">“最好的礼物还是自己动手才最有意义”</span> \r\n	</p>\r\n</span> \r\n</h3>\r\n<hr />\r\n<p class=\"\" style=\"text-align:center;\">\r\n	<span style=\"font-family:NSimSun;font-size:10px;\">花艺美学，也是手创艺术之一</span> \r\n</p>\r\n<p class=\"\" style=\"text-align:center;\">\r\n	<span style=\"font-family:NSimSun;font-size:10px;\">大家都希望能拥有一件自己创造的东西</span> \r\n</p>\r\n<p class=\"\" style=\"text-align:center;\">\r\n	<img src=\"/Public/kindeditor/attached/image/20180123/20180123164655_18020.jpg\" alt=\"\" width=\"200\" height=\"287\" title=\"\" align=\"\" /> \r\n</p>\r\n<p class=\"A\" style=\"text-align:center;\">\r\n	<span style=\"font-family:NSimSun;font-size:10px;\">如何才能插出漂亮的瓶花，</span> \r\n</p>\r\n<p class=\"A\" style=\"text-align:center;\">\r\n	<span style=\"font-family:NSimSun;font-size:10px;\">你需要的不只是新鲜的花朵、瓶子和一把剪刀，</span> \r\n</p>\r\n<p class=\"A\" style=\"text-align:center;\">\r\n	<span style=\"font-family:NSimSun;font-size:10px;\">你需要专业的色彩搭配、正确的修剪方法，</span> \r\n</p>\r\n<p class=\"A\" style=\"text-align:center;\">\r\n	<span style=\"font-family:NSimSun;font-size:10px;\">当然你更需要能够和你一起做这件事儿的人……</span> \r\n</p>\r\n<p class=\"\" style=\"text-align:center;font-size:13.3333px;\">\r\n	<b><span style=\"font-size:10px;font-family:NSimSun;\">发起人</span></b> \r\n</p>\r\n<p class=\"\" style=\"font-size:13.3333px;text-align:center;\">\r\n	<b><img src=\"/Public/kindeditor/attached/image/20180123/20180123144014_39619.jpg\" alt=\"\" width=\"100\" height=\"96\" title=\"\" align=\"\" /><br />\r\n</b> \r\n</p>\r\n<p class=\"\" style=\"font-size:13.3333px;text-align:center;\">\r\n	<strong><span style=\"font-size:10px;font-family:NSimSun;\">心灵手巧的花间艺人</span></strong> \r\n</p>\r\n<p class=\"\" style=\"font-size:13.3333px;text-align:center;\">\r\n	<strong><span style=\"color:#E53333;font-size:10px;font-family:NSimSun;\"><span style=\"font-family:NSimSun;font-size:10px;\">气质大叔；喝酒不开车；手作、花艺美学；</span></span><br />\r\n</strong> \r\n</p>\r\n<p class=\"\" style=\"font-size:13.3333px;text-align:center;\">\r\n	<br />\r\n</p>\r\n<p class=\"\" style=\"font-size:13.3333px;\">\r\n	<span style=\"font-size:10px;font-family:SimSun;\"></span> \r\n</p>\r\n<p class=\"\" style=\"font-size:13.3333px;\">\r\n	<span style=\"font-size:10px;font-family:NSimSun;\"><strong>活动细节</strong></span> \r\n</p>\r\n<p class=\"\" style=\"font-size:13.3333px;\">\r\n	<strong><span style=\"color:#E53333;font-size:10px;font-family:NSimSun;\">Time</span><span style=\"color:#E53333;font-size:10px;font-family:NSimSun;\">：</span></strong><span style=\"font-size:10px;font-family:NSimSun;\">2018-1-27（周六） 14：00 --17：00</span> \r\n</p>\r\n<p class=\"\" style=\"font-size:13.3333px;\">\r\n	<strong><span style=\"color:#E53333;\"><span style=\"font-size:10px;font-family:NSimSun;\">Add：</span><span style=\"color:#000000;font-size:12px;\"></span></span></strong><span style=\"font-size:10px;font-family:NSimSun;\">新城区尚勤路367号RUNLOLA</span> \r\n</p>\r\n<p class=\"\" style=\"font-size:13.3333px;\">\r\n	<strong><span style=\"color:#E53333;font-size:10px;font-family:NSimSun;\">活动人数：</span></strong><span style=\"font-size:10px;font-family:NSimSun;\">上限8人；</span> \r\n</p>\r\n<p class=\"\" style=\"font-size:13.3333px;\">\r\n	<span style=\"color:#E53333;font-size:10px;font-family:NSimSun;\"><strong><span style=\"font-size:10px;font-family:NSimSun;\">费用：</span><span style=\"color:#000000;\"></span></strong></span><span style=\"font-size:10px;font-family:NSimSun;\">69元/人</span> \r\n</p>\r\n<p class=\"\" style=\"font-size:13.3333px;\">\r\n	<spa